arguments.script:
(() => {
  const getNames = () => Array.from(document.querySelectorAll('.inventory_item_name')).map(el => el.textContent.trim());
  const getPrices = () => Array.from(document.querySelectorAll('.inventory_item_price')).map(el => parseFloat(el.textContent.replace('$','')));
  const results = [];
  const sortSelect = document.querySelector('.product_sort_container');
  const mapping = { az: 'Name (A to Z)', za: 'Name (Z to A)', lohi: 'Price (low to high)', hilo: 'Price (high to low)'};
  Object.keys(mapping).forEach(key => {
    sortSelect.value = key;
    sortSelect.dispatchEvent(new Event('change'));
    const names = getNames();
    const prices = getPrices();
    let pass;
    if (key === 'az') pass = JSON.stringify(names) === JSON.stringify([...names].sort());
    if (key === 'za') pass = JSON.stringify(names) === JSON.stringify([...names].sort().reverse());
    if (key === 'lohi') pass = JSON.stringify(prices) === JSON.stringify([...prices].sort((a,b)=>a-b));
    if (key === 'hilo') pass = JSON.stringify(prices) === JSON.stringify([...prices].sort((a,b)=>b-a));
    results.push({ option: mapping[key], pass });
  });
  return results;
})();

