const { test, expect } = require('@playwright/test');

test.describe.configure({ mode: 'parallel' });

// Utility for browser-specific screenshot names
function screenshotName(browserName) {
  return `logged-in-${browserName}.png`;
}

test.describe('Saucedemo Login', () => {
  test('Login and screenshot (multi-browser)', async ({ page, browserName }) => {
    console.log(`[${browserName}] Starting test...`);
    await page.goto('https://www.saucedemo.com/', { waitUntil: 'domcontentloaded' });
    console.log(`[${browserName}] Page loaded.`);
    await page.fill("input[data-test='username']", 'standard_user');
    await page.fill("input[data-test='password']", 'secret_sauce');
    await page.click("input[data-test='login-button']");
    await expect(page).toHaveURL(/.*\/inventory/);
    // Optionally wait for product element
    await page.waitForSelector('.inventory_list', { timeout: 5000 });
    await page.screenshot({ path: screenshotName(browserName), fullPage: true });
    console.log(`[${browserName}] Screenshot saved as ${screenshotName(browserName)}`);
  });
});

// playwright.config.js
/** @type {import('@playwright/test').PlaywrightTestConfig} */
module.exports = {
  testDir: '.',
  fullyParallel: true,
  reporter: [['html', { open: 'never' }], ['list']],
  use: {
    headless: false,
    viewport: { width: 1280, height: 800 },
    ignoreHTTPSErrors: true,
    screenshot: 'only-on-failure',
    video: 'off',
    trace: 'off',
    actionTimeout: 0,
    navigationTimeout: 30000,
    slowMo: 300,
  },
  projects: [
    {
      name: 'chromium',
      use: { browserName: 'chromium' },
    },
    {
      name: 'firefox',
      use: { browserName: 'firefox' },
    },
  ],
};

@echo off
REM Runs Playwright tests on Chromium and Firefox in parallel
call npm ci
npx playwright test
if exist playwright-report\index.html (
  echo Opening HTML report...
  npx playwright show-report
)

@echo off
REM Runs Playwright tests sequentially: Chromium then Firefox
call npm ci
npx playwright test --project=chromium
npx playwright test --project=firefox
if exist playwright-report\index.html (
  echo Opening HTML report...
  npx playwright show-report
)
