const { test, expect } = require('@playwright/test');

test.describe('SauceDemo Login', () => {
  test('should login and take screenshot', async ({ page, browserName }) => {
    console.log(`\n=== Starting test on: ${browserName} ===`);
    try {
      // Navigate to the login page
      await page.goto('https://www.saucedemo.com', { waitUntil: 'networkidle' });
      await expect(page).toHaveURL('https://www.saucedemo.com/');

      // Fill in credentials
      await page.fill('#user-name', 'standard_user');
      await page.fill('#password', 'secret_sauce');

      // Click login and wait for inventory page
      await Promise.all([
        page.waitForURL('**/inventory', { timeout: 10000 }),
        page.click('#login-button'),
      ]);
      await expect(page).toHaveURL(/.*inventory/);

      // Take screenshot
      const screenshotFile = `logged-in-${browserName}.png`;
      await page.screenshot({ path: screenshotFile, fullPage: true });
      console.log(`Screenshot saved: ${screenshotFile}`);
    } catch (error) {
      console.error(`Error in test on ${browserName}:`, error);
      throw error;
    }
    console.log(`=== Completed test on: ${browserName} ===\n`);
  });
});
