const { test, expect } = require('@playwright/test');

test.describe('Sauce Demo Login', () => {
  test('Test login functionality in multiple browsers', async ({ page }) => {
    console.log(`Running on: ${page.context().browser()._initializer.name}`);
    await page.goto('https://www.saucedemo.com', { waitUntil: 'load' });
    await page.fill('#user-name', 'standard_user');
    await page.fill('#password', 'secret_sauce');
    await page.click('#login-button');
    await page.waitForURL('**/inventory');
    const screenshotPath = `logged-in-${page.context().browser()._initializer.name}.png`;
    await page.screenshot({ path: screenshotPath });
    console.log(`Screenshot saved: ${screenshotPath}`);
  });
});

// playwright.config.js
const { defineConfig } = require('@playwright/test');

module.exports = defineConfig({
  testDir: '.',
  projects: [
    { name: 'chromium', use: { browserName: 'chromium' } },
    { name: 'firefox', use: { browserName: 'firefox' } },
  ],
  use: {
    headless: false,
    slowMo: 50,
    screenshot: 'only-on-failure',
    ignoreHTTPSErrors: true,
  },
  reporter: 'html',
  fullyParallel: true,
});
