const { test, expect } = require('@playwright/test');

// Utility function for logging progress and errors
function log(browserName, msg) {
  console.log(`[${browserName}] ${msg}`);
}

test.describe.parallel('Saucedemo Login Workflow', () => {
  for (const browserType of ['chromium', 'firefox']) {
    test(`Login and screenshot (${browserType})`, async ({ page, browserName }) => {
      if (browserName !== browserType) {
        test.skip();
      }
      log(browserName, 'Launching browser and navigating to the login page...');
      try {
        await page.goto('https://www.saucedemo.com', { waitUntil: 'domcontentloaded' });
        log(browserName, 'Filling login credentials...');
        await page.fill('#user-name', 'standard_user');
        await page.fill('#password', 'secret_sauce');
        await page.click('#login-button');

        log(browserName, 'Waiting for inventory/products page...');
        await expect(page).toHaveURL(/.*\/inventory/, { timeout: 10000 });
        await expect(page.locator('.inventory_list')).toBeVisible({ timeout: 10000 });
        log(browserName, 'Successfully logged in!');

        // Screenshot for each browser
        const screenshotName = `logged-in-${browserName}.png`;
        await page.screenshot({ path: screenshotName, fullPage: true });
        log(browserName, `Screenshot saved as: ${screenshotName}`);
      } catch (error) {
        log(browserName, `Test failed: ${error}`);
        throw error;
      }
    });
  }
});

