test.describe('SauceDemo Login', () => {
  test('should log in and screenshot the inventory page', async ({ page, browserName }) => {
    console.log(`[${browserName}] Navigating to SauceDemo login page...`);
    await page.goto('https://www.saucedemo.com', { waitUntil: 'domcontentloaded', timeout: 20000 });

    console.log(`[${browserName}] Filling in login credentials...`);
    await page.getByPlaceholder('Username').fill('standard_user');
    await page.getByPlaceholder('Password').fill('secret_sauce');

    console.log(`[${browserName}] Clicking login...`);
    await Promise.all([
      page.waitForURL('**/inventory*', { timeout: 10000 }),
      page.getByRole('button', { name: 'Login' }).click()
    ]);

    // Wait for main product title to ensure products page loads
    await page.waitForSelector('.inventory_list', { timeout: 10000 });

    const screenshotFile = `logged-in-${browserName}.png`;
    console.log(`[${browserName}] Saving screenshot: ${screenshotFile}`);
    await page.screenshot({ path: screenshotFile, fullPage: true });

