// saucedemo.test.js - Cross-browser test for SauceDemo login
const { test, expect } = require('@playwright/test');

// Helper for datestamped screenshot file generation
test.describe.configure({ mode: 'parallel' }); // Allow parallel and sequential run

for (const browserName of ['chromium', 'firefox']) {
  test.describe(`${browserName.toUpperCase()} SAUCEDEMO LOGIN`, () => {
    test(`should login and capture screenshot in ${browserName}`, async ({ page, browserName: runBrowser }, testInfo) => {
      if (runBrowser !== browserName) {
        test.skip();
      }
      console.log(`[${browserName}] - Navigating to saucedemo.com`);

      await page.goto('https://www.saucedemo.com', { waitUntil: 'domcontentloaded', timeout: 20000 });
      await expect(page.locator('[data-test="username"]')).toBeVisible({ timeout: 8000 }); // Reliable data-testid
      await page.fill('[data-test="username"]', 'standard_user');
      await page.fill('[data-test="password"]', 'secret_sauce');
      console.log(`[${browserName}] - Credentials entered.`);
      await Promise.all([
        page.waitForNavigation({ url: /inventory/, timeout: 15000 }),
        page.click('[data-test="login-button"]') // Reliable data-testid
      ]);
      await expect(page).toHaveURL(/.*\/inventory/);
      await expect(page.locator('.inventory_list')).toBeVisible({ timeout: 8000 }); // Validate successful login
      console.log(`[${browserName}] - Logged in, inventory page visible!`);
      const shotName = `logged-in-${browserName}.png`;
      await page.screenshot({ path: shotName, fullPage: false });
      testInfo.attach('logged-in', { path: shotName, contentType: 'image/png' });
      console.log(`[${browserName}] - Screenshot saved as ${shotName}`);
    });
  });
}
