const { test, expect } = require('@playwright/test');

const CREDENTIALS = {
  username: 'standard_user',
  password: 'secret_sauce',
};

test.describe('SauceDemo Login', () => {
  test('should log in and take screenshot [chromium|firefox]', async ({ page, browserName }) => {
    console.log(`[${browserName}] Navigating to SauceDemo login page...`);
    await page.goto('https://www.saucedemo.com', { waitUntil: 'load', timeout: 15000 });
    await expect(page.locator('input[data-test="username"]')).toBeVisible({ timeout: 5000 });
    await expect(page.locator('input[data-test="password"]')).toBeVisible({ timeout: 5000 });

    // Fill login form
    console.log(`[${browserName}] Filling login credentials...`);
    await page.fill('input[data-test="username"]', CREDENTIALS.username);
    await page.fill('input[data-test="password"]', CREDENTIALS.password);

    // Click Login button
    await Promise.all([
      page.waitForNavigation({ url: /.*\/inventory.*/, timeout: 10000 }),
      page.click('input[data-test="login-button"]'),
    ]);

    // Confirm products are displayed
    await expect(page).toHaveURL(/.*\/inventory.*/);
    await expect(page.locator('.inventory_list')).toBeVisible({ timeout: 7000 });

    // Take browser-specific screenshot
    const screenshotName = `logged-in-${browserName}.png`;
    await page.screenshot({ path: screenshotName, fullPage: true });
    console.log(`[${browserName}] Screenshot taken: ${screenshotName}`);
  });
});
