const { expect } = require('@playwright/test');
const { log, takeScreenshot, retry } = require('../utils/TestUtils');

class BasePage {
  /**
   * @param {import('@playwright/test').Page} page
   */
  constructor(page) {
    this.page = page;
    this.context = page.context();
  }

  async navigate(url) {
    log(`Navigating to: ${url}`, 'NAVIGATE');
    await retry(() => this.page.goto(url, { waitUntil: 'networkidle', timeout: 30000 }));
  }

  async click(selector) {
    log(`Click: ${selector}`);
    await retry(() => this.page.click(selector));
  }

  async type(selector, value) {
    log(`Type: ${selector} -> ${value}`);
    await retry(() => this.page.fill(selector, value));
  }

  async waitForVisible(selector, timeout = 10000) {
    log(`Wait for visible: ${selector}`);
    await this.page.waitForSelector(selector, { state: 'visible', timeout });
  }

  async waitForUrl(urlPart, timeout = 10000) {
    log(`Wait for URL to include: ${urlPart}`);
    await this.page.waitForURL((url) => url.toString().includes(urlPart), { timeout });
  }

  async captureScreenshot(name) {
    log(`Screenshot: ${name}`);
    await takeScreenshot(this.page, name); // Screenshot util auto-saves
  }

  async getText(selector) {
    await this.waitForVisible(selector);
    return await this.page.textContent(selector);
  }

  async elementExists(selector) {
    return (await this.page.$(selector)) !== null;
  }
}
module.exports = BasePage;
